sys_script_client.script:
/**
*
* This function is use in case that the record is exist and 
* if user has role of user group then user is not able to edit 
* number and assigned field . 
* if the value of valid field(boolean) return false 
* the Submission form is no longer be editable for anyone .  
*
*/

function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	 if (newValue == '') {
        return;
    }if(!g_user.hasRole('x_305921_se.admin')
	   &&  isLoading 
	   && !g_form.isNewRecord()){
			g_form.setReadonly('number', true);
			g_form.setReadonly('description', true);
			g_form.setReadonly('assigned_to', true);

	}if(isLoading && !g_form.isNewRecord() 
					   && g_form.getBooleanValue('valid') != true){
			g_form.setReadonly('number', true);
			g_form.setReadonly('assigned_to', true);
			g_form.setReadonly('description', true);
			g_form.setReadonly('work_notes', true);
	}
}
